import axios from 'axios';
import {FETCH_TODO, CHANGE_TODO_COMPLETED, CHANGE_TODO_DESCRIPTION_UPDATE, CHANGE_TODO_PRIORITY_UPDATE,
    CHANGE_TODO_RESPONSIBLE_UPDATE} from '../actions/editTodo.actions';

const initState = {
    todoDescriptionUpdate: '',
    todoPriorityUpdate: '',
    todoResponsibleUpdate: '',
    todoCompletedUpdate: false,
}

const editTodo = (state=initState, action) => {
    switch (action.type) {
        case CHANGE_TODO_DESCRIPTION_UPDATE:
            return {
                ...state,
                todoDescriptionUpdate: action.description,
            }
        case CHANGE_TODO_RESPONSIBLE_UPDATE:
                return {
                    ...state,
                    todoResponsibleUpdate: action.responsible,
                }
        case CHANGE_TODO_PRIORITY_UPDATE:
                return {
                        ...state,
                    todoPriorityUpdate: action.priority,
                }
        case FETCH_TODO:
            return action.payload;
        case CHANGE_TODO_COMPLETED: 
                return {
                    ...state,
                    todoCompletedUpdate: action.completed,
                }
        case 'update':
            const updatedTodo = {};

            for (let key in state) {
                updatedTodo[key] = state[key];
            }
            axios.post('http://localhost/todos/update/' + action.payload.id, updatedTodo)
                .then( response => {
                    console.log(response);
                });
            break;

        default:
            return state;
    }
}

export default editTodo;
