const initState = {
    todoList: [],
}

const todoList = (state = initState, action) => {
    switch(action.type) {
        case 'fetchAllTodos':
            return {
                ...state,
                todoList: action.payload,
            }
        default:
            return state;
    }
}

export default todoList;
