import axios from 'axios';

export const FETCH_ALL_TODOS = 'FETCH_ALL_TODOS';

export const fetchAllTodos = () => {
    return dispatch => {
        return axios.get('http://localhost:4000/todos')
            .then(response => {
                dispatch({type: 'fetchAllTodos', payload: response.data})
            })
            .catch(err => {
                console.log(err)
            })
    }
}
