import axios from 'axios';

export const FETCH_TODO = 'FETCH_TODO';
export const CHANGE_TODO_COMPLETED = 'CHANGE_TODO_COMPLETED';

export const changeTodoCompleted = event => {
    const completed = event.target.value;

    return {type: CHANGE_TODO_COMPLETED, completed};
}

export const fetchTodo = (id) => {
    return dispatch => {
        return axios.get('http://localhost:4000/todos/' + id)
            .then(res => {
                dispatch({type: FETCH_TODO, payload: res.data});
            })
    }
}
