const initState = {
    todoDescription: '',
    todoResponsible: '',
    todoPriority: '',
    todoCompleted: false
};

const createTodo = (state = initState, action) => {
    switch (action.type) {
        case 'changeTodoDescription':
            return {
                ...state,
                todoDescription: action.description,
            };
        case 'changeTodoResponsible':
            return {
                ...state,
                todoResponsible: action.responsible,
            }
        case 'changeTodoPriority':
            return {
                 ...state,
                todoPriority: action.priority,
            }
        case 'submit':
            console.dir(action.newTodo);

            return initState;
        default:
            return state;
    }
}

export default createTodo;
