import axios from 'axios';

const fetchAllTodos = () => {
    return dispatch => {
        console.log('It works');
        return axios.get('http://localhost:4000/todos')
            .then(response => {
                console.log(`In action`);
                console.log(response.data);
                dispatch({type: 'fetchAllTodos', payload: response.data})
            })
            .catch(err => {
                console.log(err)
            })
    }
}

export default fetchAllTodos;
