import axios from 'axios';

const initState = {
    todoDescription: '',
    todoPriority: '',
    todoResponsible: '',
    todoCompleted: false,
}

const editTodo = (state=initState, action) => {
    switch (action.type) {
        case 'changeTodoDescription':
            return {
                ...state,
                todoDescription: action.description,
            }
        case 'update':
            const updatedTodo = {};

            for (let key in state) {
                updatedTodo[key] = state[key];
            }

            axios.post('http://localhost/todos/update/' + action.payload.id, updatedTodo)
                .then( response => {
                    console.log(response);
                });
            break;
        default:
            return state;
    }
}

export default editTodo;
