const changeTodoDescription = (event) => {

    const description = event.target.value;
    return {type: 'changeTodoDescription', description}
}
export const CHANGE_TODO_DESCRIPTION = 'CHANGE_TODO_DESCRIPTION';

export default changeTodoDescription;
