import axios from 'axios';

export const changeTodoCompleted = event => {
    const completed = event.target.value;

    return {type: 'submit', completed};
}

export const FETCH_TODO_BEGIN = 'FETCH_TODO_BEGIN';
export const FETCH_TODO_SUCCESS = 'FETCH_TODO_SUCCESS';
export const FETCH_TODO_FAILERE = 'FETCH_TODO_FAILERE';

const fetchTodo = (id) => {
    return dispatch => {
        return axios.get('http://localhost:4000/todos/' + id)
            .then(res => {
                console.log(res.data);
                dispatch({type: 'fetchTodo', payload: res.data});
            })
    }
}

export default fetchTodo;
