const initState = {
    todoDescription: '',
};

const createTodo = (state = initState, action) => {
    switch (action.type) {
        case 'changeTodoDescription':
            return {
                ...state,
                todoDescription: action.description,
            };
        default:
            return state;
    }
}

export default createTodo;
