import axios from 'axios';


const initState = async () => {
    const todoListArr = axios.get('http://localhost:4000/todos').then(res => (res.data));
    const initState = {
        todoList: await todoListArr,
    }

    return initState;
}

const todoList = (state = initState, action) => {
    switch(action.type) {
        default:
            return state;
    }
}

export default todoList;
