import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import axios from 'axios';

const EditTodo = (props) => {
    const[todo, setTodo] = useState({editTodo: {}});
    console.log(props);
    // useEffect(() => {
    //     const fetchTodo = async () => {
    //         const result = await axios.get('http://localhost:4000/todos/'+ props.match.params.id)

    //         setTodo(result.data);
    //     }

    //     fetchTodo();

    // }, [props]);

    return (
        <div className='container'>
            <h3 style={{marginBottom: 20}}>Edit Todo</h3>
                <form onSubmit={props.update}>
                    <div className='form-group'>
                        <label>Description:</label>
                        <input 
                            type='text' 
                            className='form-control' 
                            onChange={props.changeTodoDescription}
                            value={props.todoDescription}
                        />
                    </div>
                    <div className='form-group'>
                        <label>Responsible:</label>
                        <input 
                            type='text' 
                            className='form-control' 
                            onChange={props.changeTodoResponsible} 
                            value={props.todoResponsible}
                        />
                    </div>
                    <div className='from-group' style={{marginBottom: 10}}>Priority:</div>
                    <div className='form-group form-check'>
                        <label className='form-check-label'>
                            <input 
                                type='radio' 
                                className='form-check-input'
                                name='priorityOptions'
                                id='priorityLow'
                                value='Low'
                                checked={props.todoPriority === 'Low'}
                                onChange={props.changeTodoPriority}/>
                            Low
                        </label>
                    </div>
                    <div className='form-group form-check'>
                        <label className='form-check-label'>
                            <input 
                                type='radio' 
                                className='form-check-input'
                                name='priorityOptions'
                                id='priorityMedium'
                                value='Medium'
                                checked={props.todoPriority === 'Medium'}
                                onChange={props.changeTodoPriority}/>
                            Medium
                        </label>
                    </div>
                    <div className='form-group form-check'>
                        <label className='form-check-label'>
                            <input 
                                type='radio' 
                                className='form-check-input'
                                name='priorityOptions'
                                id='preorityHigh'
                                value='High'
                                checked={props.todoPriority === 'High'}
                                onChange={props.changeTodoPriority}/>
                            High
                        </label>
                    </div>
                    <div className='form-group'>
                        <button type='submit' className='btn btn-secondary'>Update</button>
                    </div>
                </form>
            </div>
    )
}
export default connect()(EditTodo);
