import axios from 'axios';
import {FETCH_TODO, CHANGE_TODO_COMPLETED} from '../actions/editTodo.actions';
import {CHANGE_TODO_DESCRIPTION, CHANGE_TODO_PRIORITY, CHANGE_TODO_RESPONSIBLE} from '../actions/createTodo.actions'

const initState = {
    todoDescriptionUpdate: '',
    todoPriorityUpdate: '',
    todoResponsibleUpdate: '',
    todoCompletedUpdate: false,
}

const editTodo = (state=initState, action) => {
    switch (action.type) {
        case CHANGE_TODO_DESCRIPTION:
            return {
                ...state,
                todoDescription: action.description,
            }
        case CHANGE_TODO_RESPONSIBLE:
                return {
                    ...state,
                    todoResponsible: action.responsible,
                }
        case CHANGE_TODO_PRIORITY:
                return {
                        ...state,
                    todoPriority: action.priority,
                }
        case FETCH_TODO:
            return action.payload;
        case CHANGE_TODO_COMPLETED: 
                return {
                    ...state,
                    todoCompleted: action.completed,
                }
        case 'update':
            const updatedTodo = {};

            for (let key in state) {
                updatedTodo[key] = state[key];
            }
            axios.post('http://localhost/todos/update/' + action.payload.id, updatedTodo)
                .then( response => {
                    console.log(response);
                });
            break;

        default:
            return state;
    }
}

export default editTodo;
