import axios from 'axios';

export const FETCH_TODO = 'FETCH_TODO';
export const CHANGE_TODO_COMPLETED = 'CHANGE_TODO_COMPLETED';

export const changeTodoCompleted = event => {
    const completed = event.target.value;

    return {type: CHANGE_TODO_COMPLETED, completed};
}

export const fetchTodo = (id) => {
    return dispatch => {
        return axios.get('http://localhost:4000/todos/' + id)
            .then(res => {
                dispatch({type: FETCH_TODO, payload: res.data});
            })
    }
}

export const CHANGE_TODO_DESCRIPTION_UPDATE = 'CHANGE_TODO_DESCRIPTION_UPDATE';
export const CHANGE_TODO_PRIORITY_UPDATE = 'CHANGE_TODO_PRIORITY_UPDATE';
export const CHANGE_TODO_RESPONSIBLE_UPDATE = 'CHANGE_TODO_RESPONSIBLE_UPDATE';


export const changeTodoDescription = (event) => {

    const description = event.target.value;
    return {type: CHANGE_TODO_DESCRIPTION_UPDATE, description}
}

export const changeTodoPriority = (event) => {

    const priority = event.target.value;
    return {type: CHANGE_TODO_PRIORITY_UPDATE, priority}
}

export const changeTodoResponsible = (event) => {

    const responsible = event.target.value;

    return {type: CHANGE_TODO_RESPONSIBLE_UPDATE, responsible};
}
