const initState = {
    todoDescription: '',
    todoResponsible: '',
    todoPriority: '',
    todoCompleted: false
};

const createTodo = (state = initState, action) => {
    switch (action.type) {
        case 'changeTodoDescription':
            return {
                ...state,
                todoDescription: action.description,
            };
        case 'changeTodoResponsible':
            return {
                ...state,
                todoResponsible: action.responsible,
            }
        case 'changeTodoPriority':
                console.log(`Priority: ${state.todoPriority}`);
            return {
                 ...state,
                todoPriority: action.priority,
            }
        case 'submit':
            console.log(`Todo submited: ${state.todoDescription}`);

            return state;
        default:
            return state;
    }
}

export default createTodo;
