import axios from "axios";

const initState = {
    todoDescription: '',
    todoResponsible: '',
    todoPriority: '',
    todoCompleted: false,
    res: '',
};

const createTodo = (state = initState, action) => {
    switch (action.type) {
        case 'changeTodoDescription':
            return {
                ...state,
                todoDescription: action.description,
            };
        case 'changeTodoResponsible':
            return {
                ...state,
                todoResponsible: action.responsible,
            }
        case 'changeTodoPriority':
            return {
                 ...state,
                todoPriority: action.priority,
            }
        case 'submit':
            const submit = async (state) => {
                const newTodo = {};

                for (let key in state) {
                    newTodo[key] = state[key];
                }
            }
            break;
        default:
            return state;
    }
}

export default createTodo;
