import axios from "axios";

const initState = {
    todoDescription: '',
    todoResponsible: '',
    todoPriority: '',
    todoCompleted: false,
    res: '',
};

const createTodo = async (state = initState, action) => {
    switch (action.type) {
        case 'changeTodoDescription':
            return {
                ...state,
                todoDescription: action.description,
            };
        case 'changeTodoResponsible':
            return {
                ...state,
                todoResponsible: action.responsible,
            }
        case 'changeTodoPriority':
            return {
                 ...state,
                todoPriority: action.priority,
            }
        case 'submit':
            const newTodo = {};

            for (let key in state) {
                newTodo[key] = state[key];
            }

            const response = axios.post('http://localhost:4000/todos/add', newTodo)
                .then(res => {
                    return res;
                })
                .catch(err => {
                    return err;
                });

            return {
                ...initState,
                res: response,
            }
        default:
            return state;
    }
}

export default createTodo;
